events.inlineCode:
const thatSong = objects[0];


try 
{
    var myNotes = thatSong.getVariables().get("Notes").getAsString().split(",");
    var myIndex = thatSong.getVariables().get("Index").getAsNumber();
}
catch(err) 
{
    myOutput = "Error";
}


runtimeScene.getGame().getVariables().get("SongNote").setString(myOutput);

const playerNote = runtimeScene.getGame().getVariables().get("PlayerNote").getAsString();

const thatSong = objects[0];
const myNotes = thatSong.getVariables().get("Notes").getAsString().split(",");
const myIndex = thatSong.getVariables().get("Index");
const mySolved = thatSong.getVariables().get("Solved");

if (playerNote == myNotes[myIndex.getAsNumber()])
    {
        // If the player hit the correct note...
        myIndex.setNumber(myIndex.getAsNumber() + 1);
        if (myIndex.getAsNumber() >= myNotes.length)
        {
            // If the Index is greater than the number of notes, then the puzzle is solved.
            mySolved.setBoolean(true);
        }
    }
    else
    {
        // If the player hit the wrong note...
        myIndex.setNumber(0);
    }
