const menu = objects[0];
const behavior = menu.getBehavior(eventsFunctionContext.getBehaviorName("Behavior"));
const index = menu.getVariables().get("MenuIndex").getAsNumber();
const options = behavior._getMenuOptions().split(",");

menu.getVariables().get("MenuLength").setNumber(options.length);
menu.getVariables().get("MenuCurrent").setString(options[index]);

var output = "";
for(var i = 0; i < options.length; i++)
{
    if (i == index)
        output += options[i] + "\n";
    else
        output += "-  " + options[i] + "\n";
}

menu.getVariables().get("MenuText").setString(output);
